import React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import { Tooltip } from '@mui/material';

const Partners = () => {
  return (
    <Box className="bg-light py-5">
      <Box className="container">
        <Typography variant="h4" sx={{ color: '#1363C6', textAlign: 'center' }}>
          Partners
        </Typography>
        <Grid container spacing={5} justifyContent="center" alignItems="center" className="sponsor-logos mt-5">
          {[
            { src: './img/logos/SIFA FC.png', alt: 'Sponsor 1' },
            { src: './img/logos/AU_LOGO_ENGLISH_CMYK.png', alt: 'Sponsor 4' },
            { src: './img/logos/AUDA-NEPAD_LOGO_EN.png', alt: 'Sponsor 5' },
            { src: './img/logos/BMZ Logo.png', alt: 'Sponsor 2' },
            { src: './img/logos/EN Co-funded by the EU_PANTONE.jpg', alt: 'Sponsor 4' },
            { src: './img/logos/KfW_LOGO.png', alt: 'Sponsor 3' },
            { src: 'img/SSF_Logo_transparent_RGB.png', alt: 'Sponsor 3' },
            { src: 'img/TVETA.png', alt: 'Sponsor 3' },
            { src: 'img/ARSO-LOGO-2-pr.jpg', alt: 'Sponsor 3' },
            { src: 'img/FESTO.png', alt: 'Sponsor 3' },
            { src: 'img/SIEMENS.png', alt: 'Sponsor 3' },
          ].map((logo, index) => (
            <Grid item key={index} className="col-auto">
              <img
                src={logo.src}
                alt={logo.alt}
                className="sponsor-logo"
                style={{
                  width: '150px',
                  margin: '10px 30px',
                  padding: '50px 0',
                }}
              />
            </Grid>
          ))}
        </Grid>
      </Box>
    </Box>
  );
};


const Footer = () => {
  return (
    <Box 
      className="footer" 
      sx={{ 
        backgroundSize: 'contain', 
        padding: '40px 20px',
        backgroundColor: '#14183e',
        textDecoration: '#FFFFFF' ,
      }}
    >
      <Grid container spacing={5}>
        <Grid item md={6} lg={3}>
          <Tooltip title="Logo">
            <Typography variant="h4" sx={{ color: '#FFFFFF' }}className="mb-3 text-white">
              Virtual Mechatronics
            </Typography>
          </Tooltip>
          <Typography mb={0} sx={{ color: 'rgba(255,255,255,.5)' }}>Unlocking the potential: Virtually</Typography>
        </Grid>
        <Grid item md={6} lg={3}>
          <Typography variant="h5" sx={{ color: '#FFFFFF' }} fontFamily={'Font Awesome 5 Free'} className="mb-4 ">Get in touch</Typography>
          <Typography  sx={{color:'rgba(255,255,255,.5)'}}>
            <i  className="fa fa-map-marker-alt me-3"></i>
            <a href="map:Nyeri-Kenya" style={{color: 'rgba(255,255,255,.5'}}>Nyeri,Kenya</a>
          </Typography>
          <Typography  sx={{color:'rgba(255,255,255,.5)'}}>
            <i className="fa fa-phone-alt me-3"></i> 
            <a href="tel:+25416115027" style={{ color: 'rgba(255,255,255,.5)' }}>+25416115027</a>
          </Typography>
          <Typography  sx={{color:'rgba(255,255,255,.5)'}}>
            <i  className="fa fa-envelope me-3"></i> 
            <a href="mailto:vr.africa@dkut.ac.ke" style={{ color: 'rgba(255,255,255,.5)' }}>vr.africa@dkut.ac.ke</a>
          </Typography>
          <Box sx={{ display: 'flex', pt: 2 }} fontFamily={'Font Awesome 5 Free'}>
              <Button  
                className="btn btn-social"
                href='https://vml.dkut.ac.ke'
                sx={{
                  color: 'rgba(255,255,255,.5)',
                  border: '1px solid rgba(255,255,255,.1)',
                  borderRadius: '40px',
                  width: '40px',
                  height: '40px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  transition: '.3s',
                  backgroundColor: 'transparent',
                  '&:hover': {
                    backgroundColor: '#FFFFFF',
                    color: '#1DA1F2', // Twitter blue color
                  },
                  '&:hover .fab': {
                    color: '#1DA1F2',
                  }
                }}
              >
                <i className={"fab fa-twitter"}></i>
              </Button>
              <Button 
                className="btn btn-social"
                href='https://vml.dkut.ac.ke'
                sx={{
                  color: 'rgba(255,255,255,.5)',
                  border: '1px solid rgba(255,255,255,.1)',
                  borderRadius: '40px',
                  width: '40px',
                  height: '40px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  transition: '.3s',
                  backgroundColor: 'transparent',
                  '&:hover': {
                    backgroundColor: '#FFFFFF',
                    color: '#4267B2', // Facebook blue color
                  },
                  '&:hover .fab': {
                    color: '#4267B2',
                  }
                }}
              >
